import styles from './Register.module.css'
import * as authService from '../../services/authService.js'
import { useHistory } from 'react-router'
import { Link } from 'react-router-dom'
import { isntGuest } from '../../hoc/isntGuest.js'
import { useState } from 'react'

const Register = () => {
    const history = useHistory()

    const initialValue = { fullName: '', email: '', password: '', repeatPassword: '' }
    const [errors, setErrors] = useState(initialValue)
    const [errorArr, setErrorArr] = useState([])

    const registerSubmitHandler = (e) => {
        e.preventDefault();

        let formData = new FormData(e.currentTarget)
        let { fullName, email, password, repeatPassword } = Object.fromEntries(formData);

        authService.register(fullName, email, password, repeatPassword)
            .then(result => {
                console.log(result)

                history.push('/login')
            })
            .catch(err => {
                console.log(err)
            })
    }

    const fullNameChangeHandler = (e) => {
        let currentName = e.target.value
        if (currentName.length < 3) {
            setErrors(state => ({...state, fullName: 'You name should be at least 3 characters!'}))
            return
        }else{
            setErrors(state => ({...state, fullName: ''}))
        }
    }
    // const emailChangeHandler = (e) => {
    //     let currentEmail = e.target.value
    //     if (currentEmail.length < 3) {
    //         setErrors(state => ({...state, currentEmail: 'You name should be at least 3 characters!'}))
    //     }else{
    //         setErrors(state => ({...state, currentEmail: ''}))
    //     }
    // }
    



    return (
        <section className={styles['register-box']}>
            <h2>Register</h2>
            <form id="register" metod="POST" onSubmit={registerSubmitHandler}>
                <div className={styles['register-field']}>
                    <input type="fullName" name="fullName" id="fullName" onBlur={fullNameChangeHandler} />
                    <label>Full Name</label>
                    <span className={errors.fullName ? styles['errors'] : 'hidden'}>{errors.fullName}</span>
                </div>
                <div className={styles['register-field']}>
                    <input type="text" name="email" id="email" />
                    <label>Email</label>
                </div>
                <div className={styles['register-field']}>
                    <input type="password" name="password" id="registerPassword" required="" />
                    <label>Password</label>
                </div>
                <div className={styles['register-field']}>
                    <input type="password" name="repeatPassword" id="repeatPassword" required="" />
                    <label>Repeat Password</label>
                </div>
                <button type="submit">Submit</button>
                <div className={styles['sing-in']}>
                    <p className={styles.acc}>Already have an account?<Link to={'/login'}>Sing in</Link></p>
                </div>
            </form>
        </section>
    );
}

export default isntGuest(Register);
